'use strict';

// MODULES //

var join = require( 'path' ).join;
var mkdir = require( 'fs' ).mkdirSync;
var rmdir = require( 'fs' ).rmdirSync;
var tape = require( 'tape' );
var cwd = require( '@stdlib/process/cwd' );
var unlink = require( '@stdlib/fs/unlink' ).sync;
var readFile = require( '@stdlib/fs/read-file' ).sync;
var noop = require( '@stdlib/utils/noop' );
var evil = require( '@stdlib/utils/eval' );
var bundle = require( './../lib' );


// FIXTURES //

var FIXTURES = join( __dirname, 'fixtures', 'main' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof bundle, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if the first argument is not a string primitive', function test( t ) {
	var values;
	var i;
	values = [
		5,
		NaN,
		null,
		void 0,
		true,
		false,
		{},
		[],
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws a type error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			bundle( value, noop );
		};
	}
});

tape( 'the function throws an error if provided a callback argument which is not a function', function test( t ) {
	var values;
	var i;
	values = [
		5,
		'abc',
		NaN,
		null,
		void 0,
		true,
		false,
		{},
		[]
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws a type error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			bundle( cwd(), value );
		};
	}
});

tape( 'the function generates a bundle', function test( t ) {
	bundle( FIXTURES, onBundle );

	function onBundle( error, output ) {
		if ( error ) {
			t.fail( error.message );
		}
		if ( output === false ) {
			t.fail( 'failed to generate a bundle' );
		} else {
			try {
				evil( output.toString() );
				t.fail( 'should throw an error' );
			} catch ( err ) {
				t.equal( err.message, 'BEEP', 'has expected error message' );
			}
		}
		t.end();
	}
});

tape( 'the function generates a bundle (custom glob pattern)', function test( t ) {
	var opts = {
		'pattern': '*.js'
	};
	bundle( FIXTURES, opts, onBundle );

	function onBundle( error, output ) {
		if ( error ) {
			t.fail( error.message );
		}
		if ( output === false ) {
			t.fail( 'failed to generate a bundle' );
		} else {
			try {
				evil( output.toString() );
				t.fail( 'should throw an error' );
			} catch ( err ) {
				t.equal( err.message, 'BEEP', 'has expected error message' );
			}
		}
		t.end();
	}
});

tape( 'the function supports providing an output file path', function test( t ) {
	var opath;
	var opts;
	var dir;

	dir = join( __dirname, 'tmp' );
	mkdir( dir );

	opath = join( dir, 'bundle.js' );
	opts = {
		'out': opath,
		'pattern': '*.js'
	};
	bundle( FIXTURES, opts, onWrite );

	function onWrite( error ) {
		var out;
		if ( error ) {
			t.fail( error.message );
		}
		out = readFile( opath, {
			'encoding': 'utf8'
		});

		try {
			evil( out.toString() );
			t.fail( 'should throw an error' );
		} catch ( err ) {
			t.equal( err.message, 'BEEP', 'has expected error message' );
		}

		unlink( opath );
		rmdir( dir );

		t.end();
	}
});

tape( 'the function returns an error if unable to generate a bundle', function test( t ) {
	var dir = join( __dirname, 'fixtures', 'bad-path' );
	bundle( dir, onBundle );

	function onBundle( error ) {
		if ( error ) {
			t.ok( true, error.message );
		} else {
			t.ok( false, 'expected an error' );
		}
		t.end();
	}
});

tape( 'the function returns an error if unable to write to an output file (e.g., due to a non-existing output directory)', function test( t ) {
	var opath;
	var opts;

	opath = join( __dirname, 'nonexistant_directory', 'bundle.js' );
	opts = {
		'out': opath,
		'pattern': '*.js'
	};
	bundle( FIXTURES, opts, onWrite );

	function onWrite( error ) {
		if ( error ) {
			t.ok( true, error.message );
		} else {
			t.ok( false, 'expected an error' );
		}
		t.end();
	}
});
