tape( 'the function returns `null` if all options are valid', function test( t ) {
	var options;
	var opts;
	var err;

	opts = {};
	options = {
		'namespace': 'tree',
		'bundler': 'browserify',
		'standalone': 'stdlib',
		'requireName': 'require',
		'exportName': '@stdlib/stdlib',
		'transforms': [ noop, noop ],
		'plugins': [ noop, noop, noop ],
		'external': [ 'beep', 'boop' ],
		'paths': [ __dirname ]
	};

	err = validate( opts, options );
	t.strictEqual( err, null, 'returns null' );
	t.deepEqual( opts, options, 'sets options' );

	t.end();
});

tape( 'the function will ignore unrecognized options', function test( t ) {
	var options;
	var opts;
	var err;

	opts = {};
	options = {
		'beep': true,
		'boop': 'bop'
	};

	err = validate( opts, options );
	t.strictEqual( err, null, 'returns null' );
	t.deepEqual( opts, {}, 'ignores unrecognized options' );

	t.end();
});
