'use strict';

var Linter = require( 'eslint' ).Linter;
var rule = require( './../lib' );

var linter = new Linter();
var result;
var code;

code = [
	'/**',
	'* Squares a number.',
	'*',
	'* @param {number} x - input value',
	'* @returns {number} x*x',
	'*',
	'* @example',
	'* var y = square( 3.0 );',
	'* // returns 3.0',
	'*',
	'* y = square( 1.0 );',
	'* // returns 2.0',
	'*/',
	'function square( x ) {',
	'  return x*x;',
	'}'
].join( '\n' );

linter.defineRule( 'jsdoc-doctest', rule );

result = linter.verify( code, {
	'rules': {
		'jsdoc-doctest': 'error'
	}
});
console.log( result );
