'use strict';

// MODULES //

var remarkLint = require( 'remark-lint' );
var noDuplicateHeadings = require( 'remark-lint-no-duplicate-headings' );
var codeBlockStyle = require( 'remark-lint-code-block-style' );
var emphasisMarker = require( 'remark-lint-emphasis-marker' );
var firstHeadingLevel = require( 'remark-lint-first-heading-level' );
var headingIncrement = require( 'remark-lint-heading-increment' );
var linebreakStyle = require( 'remark-lint-linebreak-style' );
var listItemBulletIndent = require( 'remark-lint-list-item-bullet-indent' );
var listItemContentIndent = require( 'remark-lint-list-item-content-indent' );
var listItemIndent = require( 'remark-lint-list-item-indent' );
var listItemSpacing = require( 'remark-lint-list-item-spacing' );
var noParagraphContentIndent = require( 'remark-lint-no-paragraph-content-indent' );
var noTableIndentation = require( 'remark-lint-no-table-indentation' );
var noTabs = require( 'remark-lint-no-tabs' );
var orderedListMarkerStyle = require( 'remark-lint-ordered-list-marker-style' );
var strongMarker = require( 'remark-lint-strong-marker' );
var tableCellPadding = require( 'remark-lint-table-cell-padding' );
var tablePipeAlignment = require( 'remark-lint-table-pipe-alignment' );
var tablePipes = require( 'remark-lint-table-pipes' );
var unorderedListMarkerStyle = require( 'remark-lint-unordered-list-marker-style' );


// MAIN //

// Create a remark configuration:
var config = {
	'plugins': [
		[ remarkLint ],
		[ noDuplicateHeadings, [ 'error' ] ],
		[ codeBlockStyle, [ 'error', 'fenced' ] ],
		[ emphasisMarker, [ 'error', '_' ] ],
		[ firstHeadingLevel, [ 'error', 2 ] ],
		[ headingIncrement, [ 'warn' ] ],
		[ linebreakStyle, [ 'error', 'unix' ] ],
		[ listItemBulletIndent, [ 'error' ] ],
		[ listItemContentIndent, [ 'error' ] ],
		[ listItemIndent, [ 'error', 'tab-size' ] ],
		[ listItemSpacing, [ 'error' ] ],
		[ noParagraphContentIndent, [ 'error' ] ],
		[ noTableIndentation, [ 'error' ] ],
		[ noTabs, [ 'error' ] ],
		[ orderedListMarkerStyle, [ 'error', '.' ] ],
		[ strongMarker, [ 'error', '*' ] ],
		[ tableCellPadding, [ 'error', 'padded' ] ],
		[ tablePipeAlignment, [ 'error' ] ],
		[ tablePipes, [ 'error' ] ],
		[ unorderedListMarkerStyle, [ 'error', '-' ] ]
	]
};


// EXPORTS //

module.exports = config;
