'use strict';

var invalid = [];
var TAGS = require( './../../lib/defaults.json' ).tags;
var test;

test = {
	'code': [
		'/**',
		'* Squares a number.',
		'* ',
		'* @arg {number} x - input number',
		'* @returns {number} x squared',
		'*',
		'* @example',
		'* var y = square( 2.0 );',
		'* // returns 4.0',
		'*/',
		'function square( x ) {',
		'  return x*x;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': '"arg" is not one of the allowed tags ("'+TAGS.join( '","' )+'")',
			'type': null
		}
	]
};
invalid.push( test );

