'use strict';

var valid = [];
var test;

test = {
	'code': 'var err = new Error();'
};
valid.push( test );

test = {
	'code': 'var err = new Error( \'error message\' );'
};
valid.push( test );

test = {
	'code': 'var err = new TypeError( \'error message\' );'
};
valid.push( test );

test = {
	'code': 'var err = new RangeError( \'error message\' );'
};
valid.push( test );

test = {
	'code': 'var err = new SyntaxError( \'error message\' );'
};
valid.push( test );

test = {
	'code': [
		'var types = {};',
		'types.Error = Error;',
		'var err = new types.Error();'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var types = {};',
		'types.Error = Error;',
		'var err = new types[ \'Error\' ]();'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'// MAIN //',
		'',
		'function sqrt( x ) {',
		'  if ( x < 0 ) {',
		'    throw new TypeError( \'must provide a nonnegative number\' );',
		'  }',
		'  return Math.sqrt( x );',
		'}'
	].join( '\n' )
};
valid.push( test );


// EXPORTS //

module.exports = valid;
