'use strict';

var Linter = require( 'eslint' ).Linter;
var rule = require( './../lib' );

var linter = new Linter();
var result;
var code;

code = [
	'function fun() {',
	'  return 3.14;',
	'}',
	'',
	'module.exports = fun();'
].join( '\n' );

linter.defineRule( 'no-dynamic-exports', rule );

result = linter.verify( code, {
	'rules': {
		'no-dynamic-exports': 'error'
	}
});
console.log( result );
