'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'function foo() {',
		'    return 3.14;',
		'}',
		'',
		'module.exports = foo();'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Only variables, literals, and functions should be exported',
			'type': 'AssignmentExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'function foo() {',
		'    return 3.14;',
		'}',
		'',
		'export default foo();'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Only variables, literals, and functions should be exported',
			'type': 'ExportDefaultDeclaration'
		}
	],
	'parserOptions': {
		'ecmaVersion': 6,
		'sourceType': 'module'
	}
};
invalid.push( test );

test = {
	'code': [
		'module.exports = require( \'./betainc.js\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Only variables, literals, and functions should be exported',
			'type': 'AssignmentExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'var factory = require( \'./factory.js\' );',
		'',
		'module.exports = factory();'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Only variables, literals, and functions should be exported',
			'type': 'AssignmentExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'module.exports = Object[ computed ];'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Only variables, literals, and functions should be exported',
			'type': 'AssignmentExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'export default Object[ computed ];'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Only variables, literals, and functions should be exported',
			'type': 'ExportDefaultDeclaration'
		}
	],
	'parserOptions': {
		'ecmaVersion': 6,
		'sourceType': 'module'
	}
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
