'use strict';

var valid = [];
var test;

test = {
	'code': [
		'var betainc = require( \'./betainc.js\' );',
		'',
		'module.exports = betainc;'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var betainc = require( \'./betainc.js\' );',
		'',
		'export default betainc;'
	].join( '\n' ),
	'parserOptions': {
		'ecmaVersion': 6,
		'sourceType': 'module'
	}
};

test = {
	'code': [
		'var foo = \'bar\';',
		'',
		'module.exports = foo;'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var foo = \'bar\';',
		'',
		'export default foo;'
	].join( '\n' ),
	'parserOptions': {
		'ecmaVersion': 6,
		'sourceType': 'module'
	}
};

test = {
	'code': [
		'var randn = require( \'./randn.js\' );',
		'var factory = require( \'./factory.js\' );',
		'',
		'randn.factory = factory;',
		'',
		'module.exports = randn;'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var factory = require( \'./factory.js\' );',
		'',
		'var rand = factory();',
		'',
		'module.exports = rand;'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'module.exports = Object.create;'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'export default Object.create;'
	].join( '\n' ),
	'parserOptions': {
		'ecmaVersion': 6,
		'sourceType': 'module'
	}
};

test = {
	'code': [
		'module.exports = 6;'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'export default 6;'
	].join( '\n' ),
	'parserOptions': {
		'ecmaVersion': 6,
		'sourceType': 'module'
	}
};

test = {
	'code': [
		'module.exports = \'Hello World\';'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var gammaln = require( \'@stdlib/math/base/special/gammaln\' );',
		'',
		'module.exports = {',
		'	\'gammaln\': gammaln',
		'};'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'module.exports = {',
		'  \'meta\': {',
		'    \'docs\': {',
		'      \'description\': \'enforce a specified Markdown ordered list marker style in JSDoc descriptions\'',
		'    },',
		'    \'schema\': [',
		'      {',
		'        \'type\': \'string\'',
		'      }',
		'    ]',
		'  },',
		'  \'create\': main',
		'};'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'exprt default {',
		'  \'meta\': {',
		'    \'docs\': {',
		'      \'description\': \'enforce a specified Markdown ordered list marker style in JSDoc descriptions\'',
		'    },',
		'    \'schema\': [',
		'      {',
		'        \'type\': \'string\'',
		'      }',
		'    ]',
		'  },',
		'  \'create\': main',
		'};'
	].join( '\n' ),
	'parserOptions': {
		'ecmaVersion': 6,
		'sourceType': 'module'
	}
};


// EXPORTS //

module.exports = valid;
