'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;


// MAIN //

var valid = [];
var test;

test = {
	'code': [
		'// MODULES //',
		'',
		'var invalid = require( \'./invalid.js\' );'
	].join( '\n' ),
	'filename': resolve( './valid.js' )
};
valid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var math = require( \'@stdlib/math\' );'
	].join( '\n' ),
	'filename': resolve( './valid.js' )
};
valid.push( test );

test = {
	'code': [
		'import invalid from \'./invalid.js\';'
	].join( '\n' ),
	'filename': resolve( './valid.js' ),
	'parserOptions': {
		'ecmaVersion': 6,
		'sourceType': 'module'
	}
};
valid.push( test );


// EXPORTS //

module.exports = valid;
