'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'\'use strict\';',
		'// EXPORTS //',
		'',
		'module.exports = {};'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Header comment must be preceded by one additional empty line',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'\'use strict\';',
		'',
		'',
		'// EXPORTS //',
		'',
		'module.exports = {};'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Header comments must be preceded by only one empty line if they follow the `use strict` directive',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'\'use strict\';',
		'',
		'// EXPORTS //',
		'module.exports = {};'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Header comment must be followed by an empty line',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'\'use strict\';',
		'',
		'// MODULES //',
		'var os = require( \'os\' );',
		'',
		'// MAIN //',
		'',
		'/**',
		'* Returns the number of CPUs.',
		'*',
		'* @private',
		'* @returns {PositiveInteger} number of CPUs',
		'*',
		'* @example',
		'* var n = numCPUs();',
		'* // returns <number>',
		'*/',
		'function numCPUs() {',
		'	return os.cpus().length;',
		'}',
		'',
		'// EXPORTS //',
		'',
		'module.exports = numCPUs();'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Header comment must be followed by an empty line',
			'type': null
		},
		{
			'message': 'Header comment must be preceded by one additional empty line',
			'type': null
		},
		{
			'message': 'Header comment must be preceded by one additional empty line',
			'type': null
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
