'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'// EXPORTS ',
		'',
		'module.exports = {};'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Header comment must end with two trailing slashes',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// FUNCTIOns //',
		'',
		'function add( x, y ) {',
		'  return x + y;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Header comment must be in capital letters',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// EXPORT //',
		'',
		'module.exports = {};'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Invalid header comment type (must be any one of MODULES, VARIABLES, FUNCTIONS, METHODS, MAIN, EXPORTS, NOTES, FIXTURES, TESTS). Value: "EXPORT"',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// CONSTANTS //',
		'',
		'var TWO_PI = 2.0 * Math.PI;'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Invalid header comment type (must be any one of MODULES, VARIABLES, FUNCTIONS, METHODS, MAIN, EXPORTS, NOTES, FIXTURES, TESTS). Value: "CONSTANTS"',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// VARIABLES//',
		'',
		'var TWO_PI = 2.0 * Math.PI;'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Header title must be padded by exactly one space after and before comment slashes',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'//VARIABLES //',
		'',
		'var TWO_PI = 2.0 * Math.PI;'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Header title must be padded by exactly one space after and before comment slashes',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'//  VARIABLES  //',
		'',
		'var TWO_PI = 2.0 * Math.PI;'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Header title must be padded by exactly one space after and before comment slashes and should not contain any other spaces',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'//   VARIABLES //',
		'',
		'var TWO_PI = 2.0 * Math.PI;'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Header title must be padded by exactly one space after and before comment slashes and should not contain any other spaces',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// VARIABLES   //',
		'',
		'var TWO_PI = 2.0 * Math.PI;'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Header title must be padded by exactly one space after and before comment slashes and should not contain any other spaces',
			'type': null
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
