'use strict';

var valid = [];
var test;

test = {
	'code': [
		'// MODULES //',
		'',
		'var sqrt = require( \'@stdlib/math/base/special/sqrt\' );'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'// VARIABLES //',
		'',
		'var TWO_PI = 2.0 * Math.PI;'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'// FUNCTIONS //',
		'',
		'/**',
		'* Sums two elements.',
		'*',
		'* @param {number} x - first number',
		'* @param {number} y - second number',
		'* @returns {number} x + y',
		'*/',
		'function add( x, y ) {',
		'  return x + y;',
		'}'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'// METHODS //',
		'',
		'setReadOnly( Vector.prototype, \'add\', add );'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'// MAIN //',
		'',
		'var TWO_PI =  2.0 * Math.PI;'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'// EXPORTS //',
		'',
		'module.exports = {};'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'// NOTES //',
		'',
		'// This is a note...'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'// TESTS //',
		'',
		'tape( \'main export is an object\', function test( t ) {',
		'  t.ok( true, __filename );',
		'  t.equal( typeof rule, \'object\', \'main export is an object\' );',
		'  t.end();',
		'});'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'// FIXTURES //',
		'',
		'var MEDIUM = require( \'./fixtures/medium.js\' );'
	].join( '\n' )
};
valid.push( test );


// EXPORTS //

module.exports = valid;
