'use strict';

var get = require( './../lib' );

var opts = {
	'hostname': 'api.github.com',
	'pathname': '/user/repos',
	'useragent': 'my-unique-agent',
	'accept': 'application/vnd.github.moondragon+json',

	// INSERT TOKEN HERE //
	'token': '<your_token_goes_here>',

	'last_page': 'last'
};

get( opts, onResponse );

/**
* Callback invoked upon receiving a response.
*
* @private
* @param {(Error|null)} error - error or null
* @param {ObjectArray} data - response data
* @param {Object} info - rate limit info
*/
function onResponse( error, data, info ) {
	if ( info ) {
		console.error( info );
	}
	if ( error ) {
		if ( error instanceof Error ) {
			throw error;
		}
		console.error( error.message );
	} else {
		console.log( data );
	}
}
