'use strict';

// MODULES //

var licenseHeader = require( '@stdlib/_tools/licenses/header' );
var replace = require( '@stdlib/string/replace' );


// VARIABLES //

var RE_REGEXP_CHARS = /[-\/\\^$*+?.()|[\]{}]/g; // eslint-disable-line no-useless-escape


// MAIN //

/**
* Returns a license header regular expression provided a license SPDX identifier and file type.
*
* @param {string} license - license type
* @param {string} type - file type
* @throws {Error} first argument must be a supported license SPDX identifier
* @throws {TypeError} second argument must be a string
* @returns {(RegExp|null)} license header regular expression or null
*
* @example
* var re = headerRegExp( 'Apache-2.0', 'js' );
* // returns <RegExp>
*/
function headerRegExp( license, type ) {
	var h;

	// Delegate input validation to `licenseHeader`:
	h = licenseHeader( license, type );
	if ( h === null ) {
		return h;
	}
	// Escape the string:
	h = replace( h, RE_REGEXP_CHARS, '\\$&' );

	// Replace placeholder values:
	h = replace( h, '\\{\\{YEAR\\}\\}', '[0-9]{1,4}' ); // 0-9999
	h = replace( h, '\\{\\{COPYRIGHT_OWNER\\}\\}', '.+' );

	// Return a regular expression:
	return new RegExp( h );
}


// EXPORTS //

module.exports = headerRegExp;
