'use strict';

var join = require( 'path' ).join;
var readFileSync = require( '@stdlib/fs/read-file' ).sync;
var licenseHeader = require( '@stdlib/_tools/licenses/header' );
var insertHeader = require( './../lib' );

// Generate a license header:
var opts = {
	'year': 2018,
	'copyright': 'The Stdlib Authors'
};

var h = licenseHeader( 'Apache-2.0', 'fortran', opts );

// Load a Fortran file:
var fopts = {
	'encoding': 'utf8'
};

var fpath = join( __dirname, 'fixtures', 'file.f.txt' );
var file = readFileSync( fpath, fopts );

// Insert the license header:
file = insertHeader( file, h );
console.log( file );
// => <string>
