'use strict';

// MAIN //

// Note: keep list sorted in alphabetical order according to language:
var LIST = [
	// AWK:
	[ '#!/usr/bin/env awk -f', '' ], // [ <shebang>, <prepend_whitespace> ]

	// Bash/shell script:
	[ '#!/usr/bin/env bash', '' ],

	// Julia:
	[ '#!/usr/bin/env julia', '' ],

	// Node.js/JavaScript:
	[ '#!/usr/bin/env node', '\n' ],

	// Python:
	[ '#!/usr/bin/env python', '' ],

	// R:
	[ '#!/usr/bin/env Rscript', '' ]
];


// EXPORTS //

module.exports = LIST;
