'use strict';

// MODULES //

var tape = require( 'tape' );
var string2buffer = require( '@stdlib/buffer/from-string' );
var isBuffer = require( '@stdlib/assert/is-buffer' );
var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var insertHeader = require( './../lib' );


// VARIABLES //

var HEADER = '// This file is a part of stdlib. License is Apache-2.0: http://www.apache.org/licenses/LICENSE-2.0';


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof insertHeader, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if not provided a source code string or Buffer', function test( t ) {
	var values;
	var i;

	values = [
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		[],
		{},
		function noop() {}
	];
	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), Error, 'throws an error when provided ' + values[ i ] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			insertHeader( value, HEADER );
		};
	}
});

tape( 'the function throws an error if not provided a header argument which is a string or Buffer', function test( t ) {
	var values;
	var i;

	values = [
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		[],
		{},
		function noop() {}
	];
	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided ' + values[ i ] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			insertHeader( '"use strict";\n\nvar x = 3.14\n', value );
		};
	}
});

tape( 'the function inserts a license header into a source code string', function test( t ) {
	var expected;
	var actual;
	var src;

	src = [
		'"use strict";',
		'',
		'var x = 3.14;',
		''
	];
	src = src.join( '\n' );

	expected = [
		HEADER, // Note: header includes trailing newline
		src
	];
	expected = expected.join( '\n' );

	actual = insertHeader( src, HEADER );

	t.strictEqual( isString( actual ), true, 'returns a string' );
	t.strictEqual( actual, expected, 'returns expected value' );
	t.end();
});

