tape( 'the function inserts a license header after a shebang (JavaScript)', function test( t ) {
	var expected;
	var shebang;
	var actual;
	var src;

	shebang = '#!/usr/bin/env node';
	src = [
		shebang,
		'',
		'"use strict";',
		'',
		'var x = 3.14;',
		''
	];
	src = src.join( '\n' );

	expected = [
		shebang,
		'',
		HEADER,
		src.substring( shebang.length+1 ) // Note: header includes trailing newline
	];
	expected = expected.join( '\n' );

	actual = insertHeader( src, HEADER );

	t.strictEqual( actual, expected, 'returns expected value' );
	t.end();
});

