tape( 'the function inserts a license header after a shebang (Julia)', function test( t ) {
	var expected;
	var shebang;
	var actual;
	var header;
	var src;

	shebang = '#!/usr/bin/env julia';
	src = [
		shebang,
		'',
		'x = 3.14;',
		''
	];
	src = src.join( '\n' );

	header = '# ' + HEADER.substring( 2 );

	expected = [
		shebang,
		header,
		src.substring( shebang.length+1 ) // Note: header includes trailing newline
	];
	expected = expected.join( '\n' );

	actual = insertHeader( src, header );

	t.strictEqual( actual, expected, 'returns expected value' );
	t.end();
});

