tape( 'the function returns an error if an error is encountered when reading a file', opts, function test( t ) {
	var files;
	var old;

	files = [ 'dakfjdlfjsdfjasdjf/aldfkajdlfjsadl.jsdfad' ]; // File should not exist!
	old = {
		'jsdfad': 'beep boop'
	};
	updateHeader( files, old, {}, done );

	function done( err ) {
		t.strictEqual( err instanceof Error, true, 'returns an error' );
		t.end();
	}
});

tape( 'the function returns an error if an error is encountered when writing a file', opts, function test( t ) {
	var updateHeader;
	var header;
	var files;

	updateHeader = proxyquire( './../lib/main.js', {
		'@stdlib/fs/write-file': writeFile
	});

	files = [ __filename ];
	header = {
		'js': '// @license Apache-2.0\n'
	};
	updateHeader( files, {}, header, done );

	function done( err ) {
		t.strictEqual( err instanceof Error, true, 'returns an error' );
		t.end();
	}

	function writeFile() {
		var clbk = arguments[ arguments.length-1 ];
		setTimeout( onTimeout, 0 );
		function onTimeout() {
			clbk( new Error( 'beep' ) );
		}
	}
});

tape( 'the function supports providing an empty file array', opts, function test( t ) {
	updateHeader( [], {}, {}, done );

	function done( err ) {
		if ( err ) {
			t.fail( err.message );
		}
		t.end();
	}
});

tape( 'the function supports providing empty header strings', opts, function test( t ) {
	var header;
	var f;

	// Create a temporary file:
	f = tmp( false, 'single-line' );

	// Create an empty header string:
	header = '';

	// Attempt to update headers:
	updateHeader( [ f, f, f ], header, header, done );

	function done( err ) {
		var expected;
		var actual;
		if ( err ) {
			t.fail( err.message );
		} else {
			// Check that the temporary file has not changed:
			actual = readFileSync( f, 'utf8' );
			expected = readFileSync( SINGLE_LINE_HEADER, 'utf8' );
			t.strictEqual( actual, expected, 'file is unchanged' );
		}
		// Cleanup:
		cleanup();
		t.strictEqual( exists( f ), false, 'tmp file updated' );

		t.end();
	}
});

