'use strict';

// MODULES //

var tape = require( 'tape' );
var string2buffer = require( '@stdlib/buffer/from-string' );
var isBuffer = require( '@stdlib/assert/is-buffer' );
var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var updateHeader = require( './../lib' );


// VARIABLES //

var OLD_HEADER = '// @license MIT';
var NEW_HEADER = '// This file is a part of stdlib. License is Apache-2.0: http://www.apache.org/licenses/LICENSE-2.0';


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof updateHeader, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if not provided a source code string or Buffer', function test( t ) {
	var values;
	var i;

	values = [
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		[],
		{},
		function noop() {}
	];
	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), Error, 'throws an error when provided ' + values[ i ] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			updateHeader( value, OLD_HEADER, NEW_HEADER );
		};
	}
});

tape( 'the function throws an error if not provided an old header argument which is either a string, Buffer, or regular expression', function test( t ) {
	var values;
	var i;

	values = [
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		[],
		{},
		function noop() {}
	];
	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided ' + values[ i ] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			updateHeader( '// @license Apache-2.0\n\n"use strict";\n\nvar x = 3.14\n', value, NEW_HEADER );
		};
	}
});

tape( 'the function throws an error if not provided a new header argument which is either a string or Buffer', function test( t ) {
	var values;
	var i;

	values = [
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		[],
		{},
		function noop() {}
	];
	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided ' + values[ i ] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			updateHeader( '// @license Apache-2.0\n\n"use strict";\n\nvar x = 3.14\n', OLD_HEADER, value );
		};
	}
});

tape( 'the function updates a license header in a source code string', function test( t ) {
	var expected;
	var actual;
	var src;

	src = [
		OLD_HEADER,
		'',
		'"use strict";',
		'',
		'var x = 3.14;',
		''
	];
	expected = src.slice();
	expected[ 0 ] = NEW_HEADER;

	src = src.join( '\n' );
	expected = expected.join( '\n' );

	actual = updateHeader( src, OLD_HEADER, NEW_HEADER );

	t.strictEqual( isString( actual ), true, 'returns a string' );
	t.strictEqual( actual, expected, 'returns expected value' );
	t.end();
});

tape( 'the function updates a license header in a source code string (empty new header string)', function test( t ) {
	var expected;
	var actual;
	var src;

	src = [
		OLD_HEADER,
		'',
		'"use strict";',
		'',
		'var x = 3.14;',
		''
	];
	expected = src.slice( 2 );

	src = src.join( '\n' );
	expected = expected.join( '\n' );

	actual = updateHeader( src, OLD_HEADER+'\n', '' );

	t.strictEqual( isString( actual ), true, 'returns a string' );
	t.strictEqual( actual, expected, 'returns expected value' );
	t.end();
});

tape( 'the function updates a license header in a source code string (empty old header string)', function test( t ) {
	var expected;
	var actual;
	var src;

	src = [
		'"use strict";',
		'',
		'var x = 3.14;',
		''
	];
	expected = src.slice();
	expected.unshift( '' );
	expected.unshift( NEW_HEADER );

	src = src.join( '\n' );
	expected = expected.join( '\n' );

	actual = updateHeader( src, '', NEW_HEADER+'\n' );

	t.strictEqual( isString( actual ), true, 'returns a string' );
	t.strictEqual( actual, expected, 'returns expected value' );
	t.end();
});

