tape( 'the function updates a license header in a source code string (regular expression header)', function test( t ) {
	var expected;
	var actual;
	var src;

	src = [
		'// @license MIT',
		'',
		'"use strict";',
		'',
		'var x = 3.14;',
		''
	];
	expected = src.slice();
	expected[ 0 ] = NEW_HEADER;

	src = src.join( '\n' );
	expected = expected.join( '\n' );

	actual = updateHeader( src, /\/\/ @license MIT\n\n/, NEW_HEADER+'\n' );

	t.strictEqual( isString( actual ), true, 'returns a string' );
	t.strictEqual( actual, expected, 'returns expected value' );
	t.end();
});

tape( 'the function updates a multi-line license header in a source code string (regular expression header)', function test( t ) {
	var expected;
	var actual;
	var src;

	src = [
		'/**',
		'* @license Apache-2.0',
		'*/',
		'',
		'"use strict";',
		'',
		'var x = 3.14;',
		''
	];
	expected = src.slice( 4 );
	expected.unshift( NEW_HEADER );

	src = src.join( '\n' );
	expected = expected.join( '\n' );

	actual = updateHeader( src, /\/\*\*\n\* @license Apache-2\.0\n\*\/\n\n/, NEW_HEADER );

	t.strictEqual( isString( actual ), true, 'returns a string' );
	t.strictEqual( actual, expected, 'returns expected value' );
	t.end();
});

