tape( 'the function updates a license header after a shebang (JavaScript)', function test( t ) {
	var expected;
	var shebang;
	var actual;
	var src;

	shebang = '#!/usr/bin/env node';
	src = [
		shebang,
		'',
		OLD_HEADER,
		'',
		'"use strict";',
		'',
		'var x = 3.14;',
		''
	];
	expected = src.slice();
	expected[ 2 ] = NEW_HEADER;

	src = src.join( '\n' );
	expected = expected.join( '\n' );

	actual = updateHeader( src, OLD_HEADER, NEW_HEADER );

	t.strictEqual( actual, expected, 'returns expected value' );
	t.end();
});

