tape( 'the function updates a license header after a shebang (Julia)', function test( t ) {
	var expected;
	var shebang;
	var actual;
	var header;
	var src;

	shebang = '#!/usr/bin/env julia';
	header = '#\n#' + OLD_HEADER.substring( 2 );

	src = [
		shebang,
		header,
		'',
		'x = 3.14;',
		''
	];
	expected = src.slice();
	expected[ 1 ] = '#\n#' + NEW_HEADER.substring( 2 );

	src = src.join( '\n' );
	expected = expected.join( '\n' );

	actual = updateHeader( src, header, '#\n#'+NEW_HEADER.substring( 2 ) );

	t.strictEqual( actual, expected, 'returns expected value' );
	t.end();
});

