tape( 'the function updates a license header after a shebang (AWK)', function test( t ) {
	var expected;
	var shebang;
	var actual;
	var header;
	var src;

	shebang = '#!/usr/bin/env awk -f';
	header = '#' + OLD_HEADER.substring( 2 );

	src = [
		shebang,
		header,
		'',
		'BEGIN {',
		'\tcount = 0',
		'}',
		'END {',
		'\tprint count',
		'}',
		''
	];
	expected = src.slice();
	expected[ 1 ] = '#' + NEW_HEADER.substring( 2 );

	src = src.join( '\n' );
	expected = expected.join( '\n' );

	actual = updateHeader( src, header, '#'+NEW_HEADER.substring( 2 ) );

	t.strictEqual( actual, expected, 'returns expected value' );
	t.end();
});

