'use strict';

// MODULES //

var logger = require( 'debug' );
var validate = require( './validate.js' );
var getRoot = require( './root.js' );


// VARIABLES //

var debug = logger( 'lint-namespace-aliases:sync' );


// MAIN //

/**
* Synchronously lints namespace aliases.
*
* @param {Options} [options] - function options
* @param {string} [options.dir] - root directory from which to search for namespaces
* @throws {TypeError} options argument must be an object
* @throws {TypeError} must provide valid options
* @returns {(EmptyArray|ObjectArray)} lint errors
*
* @example
* var errs = lintSync();
* if ( errs.length ) {
*     console.error( JSON.stringify( errs ) );
* } else {
*     console.error( 'No detected errors' );
* }
*/
function lintSync( options ) {
	var errors;
	var opts;
	var err;
	var dir;

	opts = {};
	if ( arguments.length ) {
		err = validate( opts, options );
		if ( err ) {
			throw err;
		}
	}
	debug( 'Options: %s', JSON.stringify( opts ) );

	dir = getRoot( opts.dir || '' );
	debug( 'Base directory: %s', dir );

	errors = [];

	return errors;
}


// EXPORTS //

module.exports = lintSync;
