'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var join = require( 'path' ).join;
var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var isObjectArray = require( '@stdlib/assert/is-object-array' );
var lint = require( './../lib/sync.js' );


// VARIABLES //

var exampleDir = resolve( __dirname, '..', '..', '..', '..', 'symbol' );
var invalid = join( __dirname, 'fixtures', 'docs', 'invalid.repl.txt' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof lint, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if provided an invalid option', function test( t ) {
	t.throws( foo, TypeError, 'throws error' );
	t.end();
	function foo() {
		var opts = {
			'dir': null
		};
		lint( opts );
	}
});

tape( 'the function returns an array of lint errors if one or more files fail lint tests', function test( t ) {
	var lint;
	var errs;

	lint = proxyquire( './../lib/sync.js', {
		'glob': {
			'sync': glob
		}
	});

	errs = lint();
	t.strictEqual( isObjectArray( errs ), true, 'returns lint errors' );
	t.end();

	function glob() {
		return [ invalid, invalid ];
	}
});

tape( 'the function returns `null` if all `repl.txt` files are valid', function test( t ) {
	var opts;
	var err;

	opts = {
		'dir': exampleDir
	};
	err = lint( opts );

	t.strictEqual( err, null, 'returns `null`' );

	t.end();
});

tape( 'the function returns `null` if unable to resolve any `repl.txt` files', function test( t ) {
	var lint;
	var errs;

	lint = proxyquire( './../lib/sync.js', {
		'glob': {
			'sync': glob
		}
	});

	errs = lint();
	t.strictEqual( errs, null, 'returns null' );
	t.end();

	function glob() {
		return [];
	}
});
