'use strict';

var resolve = require( 'path' ).resolve;
var createTOC = require( './../lib' );

var opts = {
	'dir': resolve( __dirname, './../../../../string' ),
	'pattern': '*'
};

createTOC( opts, onCreate );

function onCreate( error, res ) {
	if ( error ) {
		throw error;
	}
	console.log( 'String with table of contents:\n' );
	console.log( res.toc );
	console.log( '\n' );
	console.log( 'Array of package links:\n' );
	console.log( res.links );
}
