'use strict';

// MODULES //

var fs = require( 'fs' );
var bar = require( './bar.js' );
var bool = require( './bool.js' );


// MAIN //

/**
* A dummy function.
*
* @private
* @returns {Function} a function
*/
function foo() {
	if ( bool && bar() ) {
		return fs.writeFile;
	}
	return fs.readFile;
}


// EXPORTS //

module.exports = foo;
