'use strict';

// MODULES //

var hasOwnProp = require( '@stdlib/assert/has-own-property' );


// VARIABLES //

var PROP = '__stdlib__';


// MAIN //

/**
* Filters package meta data for packages which are compatible with browser environments.
*
* @private
* @param {ObjectArray} pkgs - package meta data
* @returns {(StringArray|EmptyArray)} filtered list of package names
*/
function filter( pkgs ) {
	var out;
	var d;
	var i;

	out = [];
	for ( i = 0; i < pkgs.length; i++ ) {
		d = pkgs[ i ].data;
		if (
			!hasOwnProp( d, PROP ) ||
			!hasOwnProp( d[ PROP ], 'envs' ) ||
			!hasOwnProp( d[ PROP ].envs, 'browser' ) ||
			d[ PROP ].envs.browser
		) {
			out.push( pkgs[ i ].pkg );
		}
	}
	return out;
}


// EXPORTS //

module.exports = filter;
