'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var isStringArray = require( '@stdlib/assert/is-string-array' ).primitives;
var isArray = require( '@stdlib/assert/is-array' );
var findREPLHelp = require( './../lib/sync.js' );


// VARIABLES //

var dir = resolve( __dirname, '..', '..' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof findREPLHelp, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if provided an invalid option', function test( t ) {
	t.throws( foo, TypeError, 'throws error' );
	t.end();
	function foo() {
		var opts = {
			'dir': null
		};
		findREPLHelp( opts );
	}
});

tape( 'the function returns a string array (if at least one package REPL help file is found)', function test( t ) {
	var findREPLHelp;
	var count;
	var files;
	var opts;

	findREPLHelp = proxyquire( './../lib/sync.js', {
		'glob': {
			'sync': glob
		},
		'@stdlib/fs/exists': {
			'sync': exists
		}
	});

	count = 0;
	opts = {
		'dir': dir
	};
	files = findREPLHelp( opts );

	t.strictEqual( isStringArray( files ), true, 'returns a string array' );
	t.strictEqual( files.length, 1, 'expected length' );

	t.end();

	function glob() {
		return [
			'/beep/boop/package.json',
			'/a/b/c/d/e/f/g/h/beep/boop/bop/package.json'
		];
	}

	function exists() {
		count += 1;
		if ( count < 2 ) {
			return true;
		}
		return false;
	}
});

tape( 'the function returns an empty array if unable to find any package REPL help files', function test( t ) {
	var findREPLHelp;
	var files;

	findREPLHelp = proxyquire( './../lib/sync.js', {
		'glob': {
			'sync': glob
		},
		'@stdlib/fs/exists': {
			'sync': exists
		}
	});

	files = findREPLHelp();

	t.strictEqual( isArray( files ), true, 'returns an array' );
	t.strictEqual( files.length, 0, 'returns an empty array' );

	t.end();

	function glob() {
		return [ '/beep/boop/package.json' ];
	}

	function exists() {
		return false;
	}
});

tape( 'the function returns an empty array if unable to resolve any packages', function test( t ) {
	var findREPLHelp;
	var files;

	findREPLHelp = proxyquire( './../lib/sync.js', {
		'glob': {
			'sync': glob
		}
	});

	files = findREPLHelp();

	t.strictEqual( isArray( files ), true, 'returns an array' );
	t.strictEqual( files.length, 0, 'returns an empty array' );

	t.end();

	function glob() {
		return [];
	}
});
