'use strict';

// MODULES //

var logger = require( 'debug' );
var dirname = require( '@stdlib/utils/dirname' );
var readJSON = require( '@stdlib/fs/read-json' );


// VARIABLES //

var debug = logger( 'pkgs:types:resolve' );


// MAIN //

/**
* Inspects packages for TypeScript declarations.
*
* @private
* @param {StringArray} files - list of `package.json` files
* @param {Callback} clbk - callback to invoke upon completion
* @returns {void}
*/
function inspect( files, clbk ) {
	var total;
	var out;
	var i;

	total = files.length;
	i = -1;
	out = [];

	return next();

	/**
	* Inspects the next package.
	*
	* @private
	*/
	function next() {
		i += 1;
		debug( 'Inspecting package %d of %d: %s', i+1, total, files[ i ] );

		debug( 'Checking for TypeScript declarations...' );
		readJSON( files[ i ], onJSON );
	}

	/**
	* Callback invoked after reading a package's `package.json` file.
	*
	* @private
	* @param {(Error|null)} error - error object
	* @param {JSON} pkg - package meta data
	* @returns {void}
	*/
	function onJSON( error, pkg ) {
		var j = i + 1;
		if ( error ) {
			debug( 'Encountered an error when searching for TypeScript declarations: %s (%d of %d). Error: %s', files[ i ], j, total, error.message );
		} else if ( pkg.types ) {
			debug( 'Detected TypeScript declarations.' );
			out.push( dirname( files[ i ] ) );
		} else {
			debug( 'Unable to detect TypeScript declarations.' );
		}
		return done();
	}

	/**
	* Callback invoked upon inspecting all packages.
	*
	* @private
	* @returns {void}
	*/
	function done() {
		var j = i + 1;
		if ( j < total ) {
			debug( 'Inspected %d of %d packages.', j, total );
			return next();
		}
		debug( 'Successfully inspected all packages.' );
		return clbk( null, out );
	}
}


// EXPORTS //

module.exports = inspect;
