'use strict';

// MAIN //

/**
* Rule for enforcing that a `Parameters` section is present if the function signature is not empty.
*
* @param {Context} context - lint context
* @returns {Object} validators
*/
function main( context ) {
	/**
	* Checks whether a `Parameters` section is present if the function signature is not empty.
	*
	* @private
	* @param {Object} ast - full AST
	*/
	function hasParamSection( ast ) {
		var signature = ast.signature;
		var hasParams = signature.parameters.length > 0;
		var sections = ast.sections;
		var found = false;
		var i;

		if ( hasParams ) {
			for ( i = 0; i < sections.length; i++ ) {
				if ( sections[ i ].title === 'Parameters' ) {
					found = true;
					break;
				}
			}
			if ( !found ) {
				context.report( '`Parameters` section is missing even though the function signature is not empty', signature );
			}
		}
	}

	return {
		'function': hasParamSection
	};
}


// EXPORTS //

module.exports = main;
