'use strict';

// MODULES //

var isCapitalized = require( '@stdlib/assert/is-capitalized' );


// VARIABLES //

var RE_HYPHEN = /-/g;


// FUNCTIONS //

/**
* Counts the number of a hyphens (`-`) in a string.
*
* @private
* @param {string} str - input string
* @returns {number} number of hyphens
*/
function countHyphens( str ) {
	return ( str.match( RE_HYPHEN ) || '' ).length;
}


// MAIN //

/**
* Rule for enforcing that section headings follow style conventions.
*
* @param {Context} context - lint context
* @returns {Object} validators
*/
function main( context ) {
	/**
	* Checks whether section headings follow style conventions.
	*
	* @private
	* @param {Object} section - section to lint
	*/
	function sectionStyle( section ) {
		if ( !isCapitalized( section.title ) ) {
			context.report( 'Section title "'+section.title+'" should be capitalized', section );
		}
		if ( countHyphens( section.rawTitle ) !== section.title.length ) {
			context.report( 'Number of hyphens in the line following "'+section.title+'" must match the length of the title', section );
		}
	}
	return {
		'section': sectionStyle
	};
}


// EXPORTS //

module.exports = main;
