'use strict';

// MODULES //

var indexOf = require( '@stdlib/utils/index-of' );


// VARIABLES //

var ORDER = [
	'Parameters',
	'Returns',
	'Examples',
	'References',
	'See Also'
];


// MAIN //

/**
* Rule for enforcing that sections appear in the designated order.
*
* @param {Context} context - lint context
* @returns {Object} validators
*/
function main( context ) {
	var order = context.options[ 0 ] || ORDER;

	/**
	* Checks whether sections appear in the designated order.
	*
	* @private
	* @param {Array} sections - function documentation sections
	*/
	function sectionOrder( sections ) {
		var section;
		var last;
		var idx;
		var i;

		for ( i = 0; i < sections.length; i++ ) {
			section = sections[ i ];
			idx = indexOf( order, section.title );
			if ( last !== void 0 && idx < last ) {
				context.report( 'Sections have to be arranged in the following order: '+order.join( ', ' ), section );
			}
			last = idx;
		}
	}
	return {
		'sections': sectionOrder
	};
}


// EXPORTS //

module.exports = main;
