'use strict';

// MAIN //

/**
* Rule for enforcing that (only) the last section of a function's documentation is a 'See Also' section.
*
* @param {Context} context - lint context
* @returns {Object} validators
*/
function main( context ) {
	/**
	* Checks whether (only) the last section of a function's documentation is a 'See Also' section.
	*
	* @private
	* @param {Object} ast - full AST
	*/
	function seeAlso( ast ) {
		var section;
		var match;
		var i;

		if ( ast.sections ) {
			for ( i = 0; i < ast.sections.length; i++ ) {
				section = ast.sections[ i ];
				if ( section.title === 'See Also' ) {
					match = section;
					break;
				}
			}
		}
		if ( !match && ast.last ) {
			context.report( '"See Also" section is missing', ast );
		}
		if ( match ) {
			if ( !ast.last ) {
				context.report( 'Only the last documented function should have a "See Also" section', ast );
			}
			if ( match.content !== '' ) {
				context.report( '"See Also" section must be empty', ast );
			}
		}
	}
	return {
		'function': seeAlso
	};
}


// EXPORTS //

module.exports = main;
