'use strict';

// MODULES //

var indexOf = require( '@stdlib/utils/index-of' );


// VARIABLES //

var SECTIONS = [
	'Parameters',
	'Returns',
	'Examples',
	'References',
	'See Also'
];


// MAIN //

/**
* Rule for enforcing that only whitelisted section headers are used.
*
* @param {Context} context - lint context
* @returns {Object} validators
*/
function main( context ) {
	/**
	* Checks whether a function documentation section has a whitelisted section header.
	*
	* @private
	* @param {Object} section - any documentation section
	*/
	function whitelistedSections( section ) {
		var idx;

		idx = indexOf( SECTIONS, section.title );
		if ( idx === -1 ) {
			context.report( 'Only use one of the following section header titles: '+SECTIONS.join( '\n' ), section );
		}
	}
	return {
		'section': whitelistedSections
	};
}


// EXPORTS //

module.exports = main;
