'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var isPlainObject = require( '@stdlib/assert/is-plain-object' );
var isPlainObjectArray = require( '@stdlib/assert/is-plain-object-array' );
var pkg = require( './../package.json' ).name;
var analyze = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( b ) {
	var opts;
	var i;

	opts = {
		'dir': __dirname
	};

	i = 0;
	b.tic();

	return next();

	function next( error, o ) {
		i += 1;
		if ( error ) {
			b.fail( 'should not return an error' );
		}
		if ( i <= b.iterations ) {
			return analyze( opts, next );
		}
		b.toc();
		if ( !isPlainObject( o ) ) {
			b.fail( 'should return an object' );
		}
		b.pass( 'benchmark finished' );
		b.end();
	}
});

bench( pkg+':cumulative=true', function benchmark( b ) {
	var opts;
	var i;

	opts = {
		'dir': __dirname,
		'cumulative': true
	};

	i = 0;
	b.tic();

	return next();

	function next( error, o ) {
		i += 1;
		if ( error ) {
			b.fail( 'should not return an error' );
		}
		if ( i <= b.iterations ) {
			return analyze( opts, next );
		}
		b.toc();
		if ( !isPlainObject( o ) ) {
			b.fail( 'should return an object' );
		}
		b.pass( 'benchmark finished' );
		b.end();
	}
});

bench( pkg+':cumulative=false', function benchmark( b ) {
	var opts;
	var i;

	opts = {
		'dir': __dirname,
		'cumulative': false
	};

	i = 0;
	b.tic();

	return next();

	function next( error, o ) {
		i += 1;
		if ( error ) {
			b.fail( 'should not return an error' );
		}
		if ( i <= b.iterations ) {
			return analyze( opts, next );
		}
		b.toc();
		if ( !isPlainObjectArray( o ) ) {
			b.fail( 'should return an object array' );
		}
		b.pass( 'benchmark finished' );
		b.end();
	}
});

bench( pkg+':sync', function benchmark( b ) {
	var opts;
	var o;
	var i;

	opts = {
		'dir': __dirname
	};

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		o = analyze.sync( opts );
		if ( typeof o !== 'object' ) {
			b.fail( 'should return an object' );
		}
	}
	b.toc();
	if ( !isPlainObject( o ) ) {
		b.fail( 'should return an object' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

bench( pkg+':sync:cumulative=true', function benchmark( b ) {
	var opts;
	var o;
	var i;

	opts = {
		'dir': __dirname,
		'cumulative': true
	};

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		o = analyze.sync( opts );
		if ( typeof o !== 'object' ) {
			b.fail( 'should return an object' );
		}
	}
	b.toc();
	if ( !isPlainObject( o ) ) {
		b.fail( 'should return an object' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

bench( pkg+':sync:cumulative=false', function benchmark( b ) {
	var opts;
	var o;
	var i;

	opts = {
		'dir': __dirname,
		'cumulative': false
	};

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		o = analyze.sync( opts );
		if ( typeof o !== 'object' ) {
			b.fail( 'should return an object' );
		}
	}
	b.toc();
	if ( !isPlainObjectArray( o ) ) {
		b.fail( 'should return an object array' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});
