'use strict';

// MODULES //

var tape = require( 'tape' );
var url = require( './../lib/url.js' );


// FUNCTIONS //

function setup() {
	return {
		'protocol': 'https',
		'hostname': 'rawgit.com',
		'slug': 'stdlib-js/stdlib/develop',
		'file': 'README.md',
		'cdn': true
	};
}


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.strictEqual( typeof url, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a string', function test( t ) {
	var u = url( setup() );
	t.strictEqual( typeof u, 'string', 'returns a string' );
	t.end();
});

tape( 'the function returns a RawGit URL (cdn)', function test( t ) {
	var expected;
	var actual;

	expected = 'https://cdn.rawgit.com/stdlib-js/stdlib/develop/README.md';
	actual = url( setup() );

	t.strictEqual( actual, expected, 'returns a RawGit URL' );
	t.end();
});

tape( 'the function returns a RawGit URL (no cdn)', function test( t ) {
	var expected;
	var actual;
	var opts;

	opts = setup();
	opts.cdn = false;

	expected = 'https://rawgit.com/stdlib-js/stdlib/develop/README.md';
	actual = url( opts );

	t.strictEqual( actual, expected, 'returns a RawGit URL' );
	t.end();
});
