'use strict';

/**
* Create an (alleged) RC4 stream cipher.
*
* @module @stdlib/crypto/arc4
*
* @example
* var arc4 = require( '@stdlib/crypto/arc4' );
*
* var encrypt = arc4( 'pwd12' );
* var out = encrypt( 'beep boop bop' );
* // returns '...'
*
* var decrypt = arc4( 'pwd12' );
* var str = decrypt( out );
* // returns 'beep boop bop'
*/

// MODULES //

var arc4 = require( './arc4.js' );


// EXPORTS //

module.exports = arc4;
