tape( 'the function returns an ndarray constructor which does not require the `new` keyword', function test( t ) {
	var strides;
	var buffer;
	var offset;
	var dtype;
	var order;
	var shape;
	var arr;
	var f;

	dtype = 'generic';
	buffer = [ 1.0, 2.0, 3.0, 4.0 ];
	shape = [ 2, 2 ];
	order = 'row-major';
	strides = [ 2, 1 ];
	offset = 0;

	f = ctor( dtype, shape.length );
	arr = f( buffer, shape, strides, offset, order );

	t.strictEqual( instanceOf( arr, f ), true, 'returns an instance' );
	t.end();
});

tape( 'the function returns a memoized ndarray constructor', function test( t ) {
	var f1;
	var f2;

	f1 = ctor( 'generic', 999 );
	f2 = ctor( 'generic', 999 );
	t.strictEqual( f1, f2, 'returns expected value' );

	f1 = ctor( 'float64', 999 );
	f2 = ctor( 'float64', 999 );
	t.strictEqual( f1, f2, 'returns expected value' );

	f1 = ctor( 'float32', 999 );
	f2 = ctor( 'float32', 999 );
	t.strictEqual( f1, f2, 'returns expected value' );

	f1 = ctor( 'int16', 999 );
	f2 = ctor( 'int16', 999 );
	t.strictEqual( f1, f2, 'returns expected value' );

	f1 = ctor( 'int32', 999 );
	f2 = ctor( 'int32', 999 );
	t.strictEqual( f1, f2, 'returns expected value' );

	f1 = ctor( 'int8', 999 );
	f2 = ctor( 'int8', 999 );
	t.strictEqual( f1, f2, 'returns expected value' );

	f1 = ctor( 'uint16', 999 );
	f2 = ctor( 'uint16', 999 );
	t.strictEqual( f1, f2, 'returns expected value' );

	f1 = ctor( 'uint32', 999 );
	f2 = ctor( 'uint32', 999 );
	t.strictEqual( f1, f2, 'returns expected value' );

	f1 = ctor( 'uint8', 999 );
	f2 = ctor( 'uint8', 999 );
	t.strictEqual( f1, f2, 'returns expected value' );

	f1 = ctor( 'uint8c', 999 );
	f2 = ctor( 'uint8c', 999 );
	t.strictEqual( f1, f2, 'returns expected value' );

	f1 = ctor( 'binary', 999 );
	f2 = ctor( 'binary', 999 );
	t.strictEqual( f1, f2, 'returns expected value' );

	t.end();
});

