tape( 'the function returns an ndarray constructor which throws an error if not provided compatible input arguments', function test( t ) {
	var strides;
	var ndarray;
	var values;
	var offset;
	var buffer;
	var order;
	var dtype;
	var shape;
	var i;

	dtype = 'generic';
	buffer = [ 0.0, 1.0, 2.0, 3.0 ];
	shape = [ 2, 2 ];
	order = 'row-major';
	strides = [ 2, 1 ];
	offset = 0;

	ndarray = ctor( dtype, 2 );

	values = [
		[ [ 0.0 ], shape, strides, offset, order ],
		[ buffer, [ 3, 2 ], strides, offset, order ],
		[ buffer, shape, [ 2, 2 ], offset, order ],
		[ buffer, shape, strides, 2, order ],
		[ buffer, shape, strides, 20, order ],
		[ buffer, shape, [ 2, -1 ], offset, order ],
		[ buffer, shape, [ -2, 1 ], offset, order ],
		[ buffer, shape, [ -2, 1 ], 1, order ],
		[ buffer, shape, [ -2, -1 ], offset, order ],
		[ buffer, shape, [ -2, -1 ], 1, order ],
		[ buffer, shape, [ -2, -1 ], 2, order ]
	];
	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[ i ] ), Error, 'throws an error when provided ' + JSON.stringify( values[ i ] ) );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			ndarray.apply( null, value );
		};
	}
});
