'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var RE_BASENAME = require( './../lib' );


// TESTS //

tape( 'main export is a regular expression', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( RE_BASENAME instanceof RegExp, true, 'main export is a regular expression' );
	t.end();
});

tape( 'attached to main export is a regular expression for POSIX paths', function test( t ) {
	t.strictEqual( RE_BASENAME.posix instanceof RegExp, true, '`posix` export is a regular expression' );
	t.end();
});

tape( 'attached to main export is a regular expression for Windows paths', function test( t ) {
	t.strictEqual( RE_BASENAME.win32 instanceof RegExp, true, '`win32` export is a regular expression' );
	t.end();
});

tape( 'the main export is a POSIX specific regular expression if on a POSIX platform', function test( t ) {
	var RE_BASENAME;

	RE_BASENAME = proxyquire( './../lib', {
		'@stdlib/assert/is-windows': false
	});

	t.strictEqual( RE_BASENAME.toString(), RE_BASENAME.posix.toString(), 'main export is `posix` regexp: '+RE_BASENAME.toString() );
	t.end();
});

tape( 'the main export is a Windows specific regular expression if on a Windows platform', function test( t ) {
	var RE_BASENAME;

	RE_BASENAME = proxyquire( './../lib', {
		'@stdlib/assert/is-windows': true
	});

	t.strictEqual( RE_BASENAME.toString(), RE_BASENAME.win32.toString(), 'main export is `win32` regexp: '+RE_BASENAME.toString() );
	t.end();
});

tape( 'the `posix` regular expression captures the last part of a POSIX path', function test( t ) {
	var expected;
	var values;
	var base;
	var i;

	values = [
		'index.js',
		'/foo/bar/home.html'
	];

	expected = [
		'index.js',
		'home.html'
	];

	for ( i = 0; i < values.length; i++ ) {
		base = RE_BASENAME.posix.exec( values[ i ] )[ 1 ];
		t.strictEqual( base, expected[ i ], values[ i ] );
	}
	t.end();
});

tape( 'the `win32` regular expression captures the last part of a Windows path', function test( t ) {
	var expected;
	var values;
	var base;
	var i;

	values = [
		'index.js',
		'C:\\foo\\bar\\home.html'
	];

	expected = [
		'index.js',
		'home.html'
	];

	for ( i = 0; i < values.length; i++ ) {
		base = RE_BASENAME.win32.exec( values[ i ] )[ 1 ];
		t.strictEqual( base, expected[ i ], values[ i ] );
	}
	t.end();
});
