'use strict';

// MODULES //

var isNonNegativeIntegerArray = require( '@stdlib/assert/is-nonnegative-integer-array' );
var isNonNegativeNumberArray = require( '@stdlib/assert/is-nonnegative-number-array' );
var isUnityProbabilityArray = require( '@stdlib/assert/is-unity-probability-array' );
var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var incrspace = require( '@stdlib/math/utils/incrspace' );
var chisqCDF = require( '@stdlib/stats/base/dists/chisquare/cdf' );
var isNumber = require( '@stdlib/assert/is-number' ).isPrimitive;
var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var sample = require( '@stdlib/random/sample' );
var isnan = require( '@stdlib/assert/is-nan' );
var copy = require( '@stdlib/utils/copy' );
var gcopy = require( '@stdlib/blas/base/gcopy' );
var testStatistic = require( './statistic.js' );
var prettyPrint = require( './print.js' );
var defaults = require( './defaults.json' );
var tabulate = require( './tabulate.js' );
var getPMF = require( './get_pmf.js' );
var mult = require( './mult.js' );
var sum = require( './sum.js' );
var div = require( './div.js' );
var zeros = require( './zeros.js' );
var validate = require( './validate.js' );


// MAIN //

/**
* Performs a chi-square goodness-of-fit test.
*
* @param {NonNegativeIntegerArray} x - observation frequencies
* @param {(NonNegativeNumericArray|ProbabilityArray|string)} y - array-like object of expected frequencies or a discrete probability distribution name
* @param {...number} [args] - probability mass function (PMF) arguments
* @param {Options} [options] - function options
* @param {number} [options.alpha=0.05] - significance level
* @param {NonNegativeInteger} [options.ddof=0] - degrees of freedom adjustment
* @param {boolean} [options.simulate=false] - boolean indicating whether to compute p-values by Monte Carlo simulation
* @param {PositiveInteger} [options.iterations=500] - number of Monte Carlo iterations
* @throws {TypeError} first argument must be an array-like object containing nonnegative integers
* @throws {TypeError} second argument must be an array-like object of nonnegative integers, an array-like object of probabilities summing to one, or discrete probability distribution name
* @throws {TypeError} options argument must be an object
* @throws {TypeError} must provide valid options
* @throws {Error} observation and expected frequencies must have the same length
* @throws {Error} at least one element of `x` must be positive
* @throws {RangeError} significance level must be a number on the interval `[0,1]`
* @throws {TypeError} probability mass function (PMF) arguments must be number primitives
* @returns {Object} test results
*
* @example
* var x = [ 89, 37, 30, 28, 2 ];
* var p = [ 0.40, 0.20, 0.20, 0.15, 0.05 ];
*
* var out = chi2gof( x, p );
* // returns { 'pValue': ~0.0406, 'statistic': ~9.9901, ... }
*/
function chi2gof( x, y ) {
	var expected;
	var sopts;
	var nargs;
	var args;
	var opts;
	var psum;
	var pval;
	var stat;
	var freq;
	var pool;
	var obs;
	var err;
	var len;
	var pmf;
	var geq;
	var out;
	var df;
	var n;
	var p;
	var v;
	var i;

	obs = x;
	if ( !isNonNegativeIntegerArray( obs ) ) {
		throw new TypeError( 'invalid argument. First argument must be an array-like object containing nonnegative integers. Value: `' + obs + '`.' );
	}
	len = obs.length;
	n = sum( obs );
	if ( n === 0.0 ) {
		throw new Error( 'invalid argument. At least one element of `x` must be positive. Value: `' + obs + '`.' );
	}
	nargs = 0;
	if ( isString( y ) ) {
		pmf = getPMF( y );
		if ( pmf instanceof Error ) {
			throw pmf;
		}
		nargs += pmf.length - 1; // WARNING: this relies on PMF functions having an explicit arity
		args = [ 0 ];
		for ( i = 0; i < nargs; i++ ) {
			v = arguments[ i+2 ];
			if ( !isNumber( v ) || isnan( v ) ) {
				throw new TypeError( 'invalid argument. Probability mass function (PMF) arguments must be number primitives. Value: `' + v + '`.' );
			}
			args.push( v );
		}
		expected = [];
		psum = 0.0;
		for ( i = 0; i < len; i++ ) {
			args[ 0 ] = i;
			if ( y === 'discrete-uniform' ) {
				args[ 0 ] += args[ 1 ]; // scales the value at which to evaluate the PMF based on the minimum support of the distribution (which should have been provided as the first distribution parameter)
			}
			v = pmf.apply( null, args );
			psum += v;
			expected.push( v*n );
		}
		// Check whether we need to add a remaining category for all values greater than or equal to `len`...
		if ( psum < 1.0 ) {
			obs = gcopy( len, obs, 1, new Array( len ), 1 );
			obs.push( 0 );

			v = 1.0 - psum;
			expected.push( v*n );

			len += 1;
		}
	} else if ( isUnityProbabilityArray( y ) ) {
		p = y;
		if ( p.length !== len ) {
			throw new Error( 'invalid arguments. First and second arguments must have the same length.' );
		}
		expected = mult( new Array( len ), p, n );
	} else if ( isNonNegativeNumberArray( y ) ) {
		expected = y;
		if ( expected.length !== len ) {
			throw new Error( 'invalid arguments. First and second arguments must have the same length.' );
		}
	} else {
		throw new TypeError( 'invalid argument. Second argument must be an array-like object of probabilities summing to one, an array-like object of expected frequencies, or a discrete probability distribution name. Value: `' + y + '`.' );
	}
	opts = copy( defaults );
	if ( arguments.length > 2+nargs ) {
		err = validate( opts, arguments[ 2+nargs ] );
		if ( err ) {
			throw err;
		}
	}
	stat = testStatistic( obs, expected );
	if ( opts.simulate ) {
		if ( p === void 0 ) {
			psum = sum( expected );
			p = div( new Array( len ), expected, psum );
		}
		pool = incrspace( 0, len, 1 );
		sopts = {
			'size': n,
			'probs': p
		};
		freq = new Array( len );
		geq = 1;
		for ( i = 0; i < opts.iterations; i++ ) {
			// TODO: use `sample.factory` method once sample pkg is updated.
			v = sample( pool, sopts );
			freq = tabulate( zeros( freq ), v );
			if ( testStatistic( freq, expected ) >= stat ) {
				geq += 1;
			}
		}
		df = null;
		pval = geq / ( opts.iterations+1 );
	} else {
		df = len - 1 - opts.ddof;
		pval = 1.0 - chisqCDF( stat, df );
	}
	out = {};
	setReadOnly( out, 'rejected', pval <= opts.alpha );
	setReadOnly( out, 'alpha', opts.alpha );
	setReadOnly( out, 'pValue', pval );
	setReadOnly( out, 'df', df );
	setReadOnly( out, 'statistic', stat );
	setReadOnly( out, 'method', 'Chi-square goodness-of-fit test' );
	setReadOnly( out, 'print', prettyPrint( out ) );
	return out;
}


// EXPORTS //

module.exports = chi2gof;
