'use strict';

// MAIN //

/**
* Sets all array elements to zero.
*
* @private
* @param {Array} arr - input array
* @returns {Array} input array
*
* @example
* var arr = [ 0, 1, 1, 0, 0, 2, 3, 5 ];
*
* var out = zeros( arr );
* // returns [ 0, 0, 0, 0, 0, 0, 0, 0 ]
*
* var bool = ( arr === out );
* // returns true
*/
function zeros( arr ) {
	var i;
	for ( i = 0; i < arr.length; i++ ) {
		arr[ i ] = 0|0; // asm type annotation
	}
	return arr;
}


// EXPORTS //

module.exports = zeros;
