'use strict';

var round = require( '@stdlib/math/base/special/round' );
var randu = require( '@stdlib/random/base/randu' );
var Float64Array = require( '@stdlib/array/float64' );
var factory = require( './../lib' ).wasm;

var xbytes;
var ybytes;
var sign;
var rand;
var dabs;
var x;
var y;
var i;

// Create a new WASM instance:
dabs = factory();

// Allocate space on the heap:
xbytes = dabs.malloc( 10 * 8 ); // 8 bytes per double
ybytes = dabs.malloc( 10 * 8 );

// Create Float64Array views:
x = new Float64Array( xbytes.buffer, xbytes.byteOffset, 10 );
y = new Float64Array( ybytes.buffer, ybytes.byteOffset, 10 );

// Set data on the heap...
for ( i = 0; i < x.length; i++ ) {
	rand = round( randu()*100.0 );
	sign = randu();
	if ( sign < 0.5 ) {
		sign = -1.0;
	} else {
		sign = 1.0;
	}
	x[ i ] = sign * rand;
}
console.log( x );
console.log( y );

dabs( x.length, xbytes, 1, ybytes, -1 );
console.log( y );

// Free allocated memory:
dabs.free( xbytes );
dabs.free( ybytes );
